import React, { useEffect, useRef } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import resourceTimelinePlugin from "@fullcalendar/resource-timeline";
import interactionPlugin from "@fullcalendar/interaction";
import { Streamlit, withStreamlitConnection } from "streamlit-component-lib";

interface Event {
  id: string;
  title: string;
  start: string;
  end: string;
  resourceId?: string;
  backgroundColor?: string;
  borderColor?: string;
  extendedProps?: any;
}

interface Resource {
  id: string;
  title: string;
  businessHours?: any;
}

export interface FullCalendarComponentProps {
  args?: {
    events: Event[];
    resources?: Resource[];
    view: string;
    initialDate?: string;
    height: number;
    headerToolbar: {
      left: string;
      center: string;
      right: string;
    };
  };
}

const DEFAULT_ARGS: FullCalendarComponentProps["args"] = {
  events: [],
  view: "resourceTimelineWeek",
  height: 600,
  headerToolbar: {
    left: "prev,next today",
    center: "title",
    right: "resourceTimelineWeek,resourceTimelineMonth",
  },
};

const FullCalendarComponent: React.FC<FullCalendarComponentProps> = ({
  args: argsProp,
}) => {
  const args = argsProp ?? DEFAULT_ARGS;
  const calendarRef = useRef<FullCalendar>(null);

  useEffect(() => {
    // Notify Streamlit that component is ready
    Streamlit.setFrameHeight();
  }, []);

  const handleEventClick = (info: any) => {
    // Send event data back to Streamlit
    Streamlit.setComponentValue({
      type: "eventClick",
      event: {
        id: info.event.id,
        title: info.event.title,
        start: info.event.start?.toISOString(),
        end: info.event.end?.toISOString(),
        resourceId: info.event.getResources()[0]?.id,
        extendedProps: info.event.extendedProps,
      },
    });
  };

  const handleDateClick = (info: any) => {
    // Send date click data back to Streamlit
    Streamlit.setComponentValue({
      type: "dateClick",
      date: info.dateStr,
      resourceId: info.resource?.id,
    });
  };

  const calendarOptions = {
    ref: calendarRef,
    plugins: [
      dayGridPlugin,
      timeGridPlugin,
      resourceTimelinePlugin,
      interactionPlugin,
    ],
    initialView: args.view,
    initialDate: args.initialDate,
    headerToolbar: args.headerToolbar,
    events: args.events,
    resources: args.resources,
    editable: false,
    selectable: true,
    selectMirror: true,
    dayMaxEvents: true,
    eventClick: handleEventClick,
    dateClick: handleDateClick,
    height: args.height,
    resourceAreaHeaderContent: "Resources",
    resourceAreaWidth: "20%",
    slotMinTime: "08:00:00",
    slotMaxTime: "18:00:00",
    allDaySlot: false,
    nowIndicator: true,
    eventTimeFormat: {
      hour: "2-digit",
      minute: "2-digit",
      hour12: false,
    },
    slotLabelFormat: {
      hour: "2-digit",
      minute: "2-digit",
      hour12: false,
    },
  };

  return (
    <div style={{ height: args.height, width: "100%" }}>
      <FullCalendar
        {...(calendarOptions as React.ComponentProps<typeof FullCalendar>)}
      />
    </div>
  );
};

const ConnectedFullCalendar = withStreamlitConnection(FullCalendarComponent);
export default ConnectedFullCalendar as React.FC<FullCalendarComponentProps>;
